## Solução de uma EDO usando Metodo implicito de EULER + Met de Newtonclear all;close all;clc;a=0;b=0.5;h=0.002; %passosN=(b-a)/h;n(1) = 2000;t(1) = a;for i=1:N  t(i+1) = t(i)+h;  x = n(i);    % Met Newton  for j=1:20    num = x + 0.800 * x^(3/2) * h - 10 * n(1) * (1-exp(-3*t(i+1))) * h-n(i);    den = 1 + 0.800 * 1.5 * x^(1/2) * h;    xnew = x - (num/den);        if abs((xnew-x)/x) < 1e-4      break    else      x = xnew;    endif      endfor    if j==20    fprint('Sem solução numerica em t = %g s', t(i))    break  endif    % Fim de Newton  n(i+1) = xnew;endforplot(t,n);grid on;xlabel('t(s)');ylabel('n')